mapper.select:
select 

from PT_CONFIG_ITEMS



      and CONFIG_MAIN_ID = #{params.configMainId,jdbcType=INTEGER}


      and ITEM_DESC = #{params.itemDesc,jdbcType=VARCHAR}


      and ITEM_IS_CRYPT = #{params.itemIsCrypt,jdbcType=INTEGER}


      and ITEM_KEY = #{params.itemKey,jdbcType=VARCHAR}


      and ITEM_VALUE = #{params.itemValue,jdbcType=VARCHAR}


      and ITEM_SEQ = #{params.itemSeq,jdbcType=INTEGER}


      and IS_EXTENDS = #{params.isExtends,jdbcType=INTEGER}


      and EXTENDS_ID = #{params.extendsId,jdbcType=INTEGER}


      and ID = #{params.id,jdbcType=INTEGER}



ORDER BY ITEM_SEQ

    SELECT
		A.*, C.PRODUCT_MARK, D.MODULE_MARK
	FROM
		PT_CONFIG_ITEMS A
	LEFT JOIN PT_CONFIG_MAIN B ON A.CONFIG_MAIN_ID = B.ID
	LEFT JOIN PT_CONFIG_PRODUCT C ON C.ID = B.PRODUCT_ID
	LEFT JOIN PT_CONFIG_MODULES D ON D.ID = B.MODULE_ID



          and B.PRODUCT_ID = #{params.productId,jdbcType=INTEGER}


          and B.MODULE_ID = #{params.moduleId,jdbcType=INTEGER}


          and A.CONFIG_MAIN_ID = #{params.configMainId,jdbcType=INTEGER}


          and A.EXTENDS_ID = #{params.extendsId,jdbcType=INTEGER}


          and A.IS_EXTENDS = #{params.isExtends,jdbcType=INTEGER}

delete.if:
DELETE FROM PT_CONFIG_ITEMS WHERE CONFIG_MAIN_ID IN ( SELECT ID FROM PT_CONFIG_MAIN
and PRODUCT_ID = #{params.productId,jdbcType=INTEGER}
and MODULE_ID = #{params.moduleId,jdbcType=INTEGER}
)
