mapper.select:
select 

from PT_CONFIG_MAIN



      and PRODUCT_ID = #{params.productId,jdbcType=INTEGER}


      and MODULE_ID = #{params.moduleId,jdbcType=INTEGER}


      and CONFIG_TYPE = #{params.configType,jdbcType=VARCHAR}


      and CREATE_UUID = #{params.createUuid,jdbcType=VARCHAR}


      and EXTENDS_ID = #{params.extendsId,jdbcType=INTEGER}


      or EXTENDS_ID = #{params.orExtendsId,jdbcType=INTEGER}


      and ID = #{params.id,jdbcType=INTEGER}

delete.if:
	DELETE FROM PT_CONFIG_MAIN


        and PRODUCT_ID = #{params.productId,jdbcType=INTEGER}


        and MODULE_ID = #{params.moduleId,jdbcType=INTEGER}
