mapper.insert:
 insert into PT_CONFIG_TEMPLATE (ID, TEMPLATE_NAME, TEMPLATE_DESC, 
   CONFIG_TYPE, CREATE_UUID, IS_INNER
   )
 values (#{id,jdbcType=INTEGER}, #{templateName,jdbcType=VARCHAR}, #{templateDesc,jdbcType=VARCHAR}, 
   #{configType,jdbcType=VARCHAR}, #{createUuid,jdbcType=VARCHAR}, #{isInner,jdbcType=INTEGER}
   )

	      SELECT LAST_INSERT_ROWID() AS id

mapper.select:
select 

from PT_CONFIG_TEMPLATE



      and TEMPLATE_NAME like '%' || #{params.templateName,jdbcType=VARCHAR} || '%'


      and TEMPLATE_DESC = #{params.templateDesc,jdbcType=VARCHAR}


      and CONFIG_TYPE = #{params.configType,jdbcType=VARCHAR}


      and CREATE_UUID = #{params.createUuid,jdbcType=VARCHAR}


      and IS_INNER = #{params.isInner,jdbcType=INTEGER}


      and ID = #{params.id,jdbcType=INTEGER}

select 

from PT_CONFIG_TEMPLATE



      and TEMPLATE_NAME like '%' || #{params.templateName,jdbcType=VARCHAR} || '%'


      and TEMPLATE_DESC = #{params.templateDesc,jdbcType=VARCHAR}


      and CONFIG_TYPE = #{params.configType,jdbcType=VARCHAR}


      and CREATE_UUID = #{params.createUuid,jdbcType=VARCHAR}


      or IS_INNER = #{params.isInner,jdbcType=INTEGER}


      and ID = #{params.id,jdbcType=INTEGER}

	select 

  from PT_CONFIG_TEMPLATE



        and CREATE_UUID = #{params.createUuid,jdbcType=VARCHAR}


        or IS_INNER = #{params.isInner,jdbcType=INTEGER}

select 

from PT_CONFIG_TEMPLATE WHERE TEMPLATE_NAME = #{params.templateName,jdbcType=VARCHAR} 
AND (CREATE_UUID = #{params.createUuid,jdbcType=VARCHAR} OR IS_INNER = 1)

  and ID != #{params.id,jdbcType=INTEGER}
