      and app_name = #{params.appName,jdbcType=VARCHAR}


      and app_name like  '%'||#{params.appNameLike,jdbcType=VARCHAR}||'%'


      and app_ip_url = #{params.appIpUrl,jdbcType=VARCHAR}


      and is_approve = #{params.isApprove,jdbcType=VARCHAR}


      and app_alias = #{params.appAlias,jdbcType=VARCHAR}


      and app_alias like '%'||#{params.appAliasLike,jdbcType=VARCHAR}||'%'


      and remark = #{params.remark,jdbcType=VARCHAR}


      and con_ip = #{params.conIp,jdbcType=VARCHAR}


      and con_ip like  '%'||#{params.conIpLike,jdbcType=VARCHAR}||'%'


      and con_port = #{params.conPort,jdbcType=INTEGER}


      and is_use_url = #{params.isUseUrl,jdbcType=VARCHAR}


      and id = #{params.id,jdbcType=VARCHAR}



order by id desc

